<?php namespace App;	class Login 	{	private $db;	private $user;		public function __construct(IConn $db, $user)			{				$this->db = $db->connect();				$this->user = $user;			}		public function findLogin()			{				$query = "SELECT * FROM usuarios WHERE `email` = ? AND `senha` = ?";				$stmt = $this->db->prepare($query);				$stmt->bindValue(1, $this->user->getUser());				$stmt->bindValue(2, $this->user->getPass());				$stmt->execute();								return $stmt->fetch(\PDO::FETCH_OBJ);			}		public function updateLogin($status)			{			$query = "UPDATE usuarios set `online` = ? WHERE `email` = ? AND `senha` = ?";			$stmt = $this->db->prepare($query);			$stmt->bindValue(1, $status);			$stmt->bindValue(2, $this->user->getUser());			$stmt->bindValue(3, $this->user->getPass());			$stmt->execute();			}		public function isOnline()			{			}		} ?>