<?php 	namespace App;	class ServiceOrder 	{		private $db;		private $order;		private $poloniex;		public function __construct(IConn $db, IOrder $order, $poloniex)		{			$this->db = $db->connect();			$this->order = $order;			$this->poloniex = $poloniex;		}		public function listOrders(int $config_id)		{			$query = "SELECT * FROM ordens WHERE `configuracoes_id` = ?";			$stmt = $this->db->prepare($query);			$stmt->bindValue(1, $config_id);			$stmt->execute();			return $stmt->fetchAll(\PDO::FETCH_OBJ);		}		public function listEngine()		{			$qr = "SELECT * FROM `config` WHERE `id_user` = ?";			$stmt = $this->db->prepare($qr);			$stmt->bindValue(1, $this->order->getIdUser());			$stmt->execute();			return $stmt->fetchAll(\PDO::FETCH_OBJ);		}		public function getEngine(int $id)		{			$query = "SELECT * FROM orders WHERE `id` = ?";			$stmt = $this->db->prepare($query);			$stmt->bindValue(1, $id);			$stmt->execute();			return $stmt->fetchAll(\PDO::FETCH_OBJ);		}		public function saveEngine()		{			$qr = "SELECT * FROM `config` WHERE `id_user` = ? && `pair` = ?";			$stmt = $this->db->prepare($qr);			$stmt->bindValue(1, $this->order->getIdUser());			$stmt->bindValue(2, $this->order->getPair());			$stmt->execute();		}		public function balances()		{			return $this->poloniex->get_ticker('ALL');		}		public function findBalance()		{			$pair = $this->order->getPair();			return $this->poloniex->get_ticker('ALL');		}			public function getOrderBook($pair)		{			return $this->poloniex->get_order_book($pair);		}		public function cancelOrders($pair, $order)		{			return $this->poloniex->cancel_order($pair, $order);		}		public function Ordens($pair)		{			 return $this->poloniex->get_open_orders($pair);		}			} ?>