<?php namespace App;class ServiceUser{	private $db;	private $user;	private $poloniex;	public function __construct(IConn $db, $user, $poloniex)	{		$this->db = $db->connect();		$this->user = $user;		$this->poloniex = $poloniex;	}	public function update()	{		$query = "UPDATE usuarios set `key` = ?, `secret` = ? WHERE `email` = ? AND `senha` = ?";		$stmt = $this->db->prepare($query);		$stmt->bindValue(1, $this->user->getKey());		$stmt->bindValue(2, $this->user->getSecret());		$stmt->bindValue(3, $this->user->getUser());		$stmt->bindValue(4, $this->user->getPass());		return $stmt->execute();	}	public function getBalance($balance)	{		return $this->poloniex->get_balances()[$balance];	}} ?>