<?php namespace App;	class Trade 	{	private $db;	private $user;		public function __construct(IConn $db, $user)		{			$this->db = $db->connect();			$this->user = $user;		}		public function access()		{			$query = "SELECT * FROM usuarios WHERE `email` = ? AND `senha` = ?";			$stmt = $this->db->prepare($query);			$stmt->bindValue(1, $this->user->getUser());			$stmt->bindValue(2, $this->user->getPass());			$stmt->execute();			return $stmt->fetch(\PDO::FETCH_OBJ);		}		} ?>