<?php  namespace App;session_start();//require_once 'IConn.php';require_once '../vendor/autoload.php';if(isset($_GET['email']) && isset($_GET['pass'])){  include_once 'conexao.php';         $db = new \App\Conn(HOST_SERVER, DB_SERVER, USER_SERVER, PASS_SERVER);	$user = new \App\User;	$user->setUser($_GET['email'])->setPass($_GET['pass']);	$login = new \App\Login($db, $user);		if($login->findLogin())	{		$_SESSION['username'] = $login->findLogin()->email;		$_SESSION['pass'] = $login->findLogin()->senha;        	$_SESSION['id'] = $login->findLogin()->id;		$login->updateLogin("online");		echo json_encode(true);			}else{		echo json_encode('Error: e-mail ou login incorreto!');	}}else{	echo json_encode('Preencha os campos de e-mail e senha!');} ?>